catalog_script_client.script:
//This function both clears the seconds value on the Start Date/Time field
//   but also defaults the End Date/Time field to Start + 1 hour

function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    //Clear second from Start field
    var date = newValue.split(' ')[0];
    var time = newValue.split(' ')[1].split(':'); // time[0] = hours, time[1] = minutes
    var ampm = newValue.split(' ')[2];
    var finalValue = date + ' ' + time[0] + ':' + time[1] + ':' + '00 ' + ampm;
    g_form.setValue('function_start_date_time', finalValue);

    //Default End Date/Time field to Start Date/Time + 1 hour
    if (g_form.getValue('function_end_date_time') == '') {
        var hours = (Number(time[0]) + 1).toString().padStart(2, '0');
        g_form.setValue('function_end_date_time', date + ' ' + hours + ':' + time[1] + ':' + '00 ' + ampm);
    }
}
